    const payload = JSON.parse(e.postData.contents || "{}");

    // Seguran√ßa simples
    if (payload.token !== TOKEN) {
      return ContentService
        .createTextOutput(JSON.stringify({ ok: false, error: "Unauthorized" }))
        .setMimeType(ContentService.MimeType.JSON);
    }

const ss = SpreadsheetApp.openById(SPREADSHEET_ID);

const debug = ss.getSheetByName("Debug") || ss.insertSheet("Debug");
debug.appendRow([new Date(), "doPost chamado", e?.postData?.contents?.length || 0]);

const sh = ss.getSheetByName(SHEET_NAME) || ss.insertSheet(SHEET_NAME);    

    // Cabe√ßalho (primeira execu√ß√£o)
    if (sh.getLastRow() === 0) {
      sh.appendRow([
        "Data","Nome","WhatsApp","CPF/CNPJ","Email",
        "Rua","N¬∫","Bairro","Cidade","UF","CEP","Observa√ß√µes",
        "SKU","Produto",'Pre√ßo','Qtd','Total Item','Total Pedido'
      ]);
    }


    const createdAt = payload.createdAt || new Date().toISOString();
    const totalPedido = payload.total ?? "";

    const c = payload.customer || {};
    const a = c.address || {};

    const items = Array.isArray(payload.items) ? payload.items : [];



    const rows = items.map((it) => {
      const preco = Number(it.preco) || 0;
