        a.neighborhood || "",
        a.city || "",
        a.state || "",
        a.zip || "",
        c.notes || "",
        it.sku || "",
        it.nome || "",
        preco,
        qty,
        preco * qty,
        totalPedido
      ];
    });

    // üîç DEBUG ‚Äî verifica se est√° chegando sem itens
if (!rows.length) {
  const debug = ss.getSheetByName("Debug") || ss.insertSheet("Debug");
  debug.appendRow([
    new Date(),
    "TESTANDO",
    JSON.stringify(payload).slice(0, 500)
  ]);
}

    if (rows.length) {
      sh.getRange(sh.getLastRow() + 1, 1, rows.length, rows[0].length).setValues(rows);
    }

    return ContentService
      .createTextOutput(JSON.stringify({ ok: true, rows: rows.length }))
      .setMimeType(ContentService.MimeType.JSON);

  } catch (err) {
    return ContentService
      .createTextOutput(JSON.stringify({ ok: false, error: String(err) }))
      .setMimeType(ContentService.MimeType.JSON);
  }
}

const Repositories = {
  getSpreadsheet_: function () {
    return SpreadsheetApp.openById(SPREADSHEET_ID);
  },

  getOrdersSheet_: function () {
    const ss = this.getSpreadsheet_();
    let sh = ss.getSheetByName("Pedidos");

    if (!sh) {
      sh = ss.insertSheet("Pedidos");
      sh.appendRow([
        "ID", "Data", "Nome", "WhatsApp", "CPF/CNPJ", "Email",
        "Endere√ßo", "Itens (JSON)", "Total", "Status", "Obs"
      ]);
    }
    return sh;
  },

  // ‚úÖ ESTE √â O saveOrder
  saveOrder: function (pedido) {
    if (!pedido?.cliente?.nome || !pedido?.cliente?.telefone) {
      throw new Error("Campos obrigat√≥rios: cliente.nome e cliente.telefone");
    }

    const sh = this.getOrdersSheet_();
    const id = Utilities.getUuid();
    const data = new Date();

    sh.appendRow([
      id,
      data,
      pedido.cliente.nome,
      pedido.cliente.telefone,
      pedido.cliente.doc || "",
      pedido.cliente.email || "",
      pedido.endereco || "",
      JSON.stringify(pedido.itens || []),
      Number(pedido.total || 0),
      pedido.status || "Pendente",
      pedido.obs || ""
    ]);

    return { success: true, id };
  },

  getPedidos: function () {
    const sh = this.getOrdersSheet_();
    const values = sh.getDataRange().getValues();
    if (values.length <= 1) return [];

    const headers = values.shift();
    return values.map(row => {
      const obj = {};
      headers.forEach((h, i) => (obj[h] = row[i]));
      return obj;
    });
  }
};
